import React, {Component} from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import clsx from 'clsx';

//file-upload-drop-zone
import Dropzone from 'react-dropzone';
import '../../assets/css/dataupload.css'

const useStyles = makeStyles((theme) => ({
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 500,
  },
}));

export default function DataUpload() {
  const classes = useStyles();
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  return (
        <>
          <Grid container spacing={2}>
            <Grid item xs={12}>
                    <Paper className={fixedHeightPaper}>
                      <Basic />
                    </Paper>
            </Grid>
          </Grid>
        </>
  );
}

{/* dropzone basic */}
class Basic extends Component {
  constructor() {
    super();
    this.onDrop = (files) => {
      this.setState({files})
    };
    this.state = {
      files: []
    };
  }

  imgUpload(e){
    e.preventDefault();
    const api = axios.create({
      baseURL: 'http://localhost:8080'
    })
    var frm = new FormData();
    var photoFile = document.getElementById("file");
    console.log(photoFile)
    /*frm.append("file", photoFile.files[0]);
    console.log(photoFile.files);

    api.post('/data', frm, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
      }).then(function (response) {
        alert("right")
        console.log(response);
      }).catch(function (error) {
        alert("error");
        console.log(error);
      });
      */
  }

  render() {
    const files = this.state.files.map(file => (
      <li key={file.name}>
        {file.name} - {file.size} bytes
      </li>
    ));


    return (
      <Dropzone onDrop={this.onDrop}>
        {({getRootProps, getInputProps}) => (
          <section >
            <div {...getRootProps({className: 'dropzone'})}>
              <input id="file" {...getInputProps()} />
              <h3><strong>파일들을 끌어다 놓거나, 클릭하여 자신의 데이터들을 선택하세요</strong></h3>
            </div>
            <aside>
              <h4>Files</h4>
              <ul>{files}</ul>
            </aside>
            <center><Button onClick={this.imgUpload.bind(this)} variant="contained">업로드하기</Button></center>
          </section>
        )}
      </Dropzone>
    );
  }
}
